local rdOn = false
local paginas = math.ceil(#list/10)

local function exibir_pagina(pagina, list, name)
    term.clear()
    term.setCursorPos(1,1)
    term.setTextColor(32)
    -- C√°lculo do √≠ndice inicial e final dos itens a serem exibidos
    local inicio = (pagina - 1) * 10 + 1
    local fim = inicio + 9

    -- Verifica√ß√£o para n√£o ultrapassar o tamanho da tabela
    if fim > #list then
      fim = #list
    end

    local QuantPaginas = math.ceil(#list/10)

    term.setTextColor(4)
    local tamanhoPalavra = string.len(name)
    local espacosAntes = math.floor((24 - tamanhoPalavra) / 2)
    local espacos = string.rep(" ", espacosAntes)
    local textoFormatado = espacos .. name
    print(textoFormatado.."\n")
    term.setTextColor(32)
    -- Exibi√ß√£o dos itens da p√°gina atual
    for i = inicio, fim do
        if(#list[i].name > 20) then
      print(i..". "..string.sub(list[i].name, 1, 19).."...")
        else
            print(i..". "..list[i].name)
        end
    end
    
    term.setTextColor(4)
    print("\n?. Ajuda?\n")
    term.setTextColor(1)
    term.setCursorPos(1,16)
    print("> page: "..pagina.."/"..QuantPaginas)
    print("\n   <<   <   ||   >   >>\n")
end

function playMusic(autor, name, link)
    term.clear()
    term.setCursorPos(1,1)
    term.setTextColor(16)
    print("|-----World Break FM-----|\n|________________________|\n\n")
    print("         Tocando \n\nAutor: "..autor.."\nNome: "..name.."\n\n")
    print("   <<   <   ||   >   >>")
    term.setTextColor(32768)
    shell.run("speaker play "..link)
end

function help()
    term.clear()
    term.setCursorPos(1,1)
    term.setTextColor(4)
    print("    _____ __  __ _____  ")
    print("   / ____|  \\/  |  __ \\ ")
    print("  | |    | \\  / | |  | |")
    print("  | |    | |\\/| | |  | |")
    print("  | |____| |  | | |__| |")
    print("   \\_____|_|  |_|_____/\n\n")
    local cmd = {"Next    (>)","Back    (<)","Esc     (.)","Random  (rd)","Sobre   (i)","Ajuda   (?)"}
    print("")

    term.setTextColor(1)
    for comando = 1, #cmd, 1 do
        textutils.slowPrint(" "..cmd[comando])
    end
end

local inThePage = 1 -- Paginas
local PlaylistSelected = 0
local inThePlaylists = 1

exibir_pagina(inThePage, list, "Playlists")
while true do
    local entrada = read()

    -- Sistema de pagina√ß√£o
    if tonumber(entrada) then
        if PlaylistSelected == 0 then -- Entra na Playlists
            PlaylistSelected = tonumber(entrada)
        else
            playMusic(list[PlaylistSelected].name, list[PlaylistSelected].playlist[tonumber(entrada)].name, list[PlaylistSelected].playlist[tonumber(entrada)].link)
            tocando = true
        end
    elseif entrada == "back" or entrada == "<" then
        if PlaylistSelected > 1 then
            if inThePlaylists > 1 then
                inThePlaylists = inThePlaylists - 1
            else
                PlaylistSelected = 0
            end
        elseif PlaylistSelected == 1 then
            PlaylistSelected = 0 -- Remover
        else
            if inThePage > 1 then
                inThePage = inThePage - 1
            end
        end
    elseif entrada == "next" or entrada == ">" then
        if PlaylistSelected >= 1 then
            if inThePlaylists < math.ceil(#list[PlaylistSelected].playlist/10) then
                inThePlaylists = inThePlaylists + 1;
            end
        else 
            if inThePage < math.ceil(#list/10) then
                inThePage = inThePage + 1;
            end
        end
    elseif entrada == "esc" then
        PlaylistSelected = 0
        inThePlaylists = 1
        inThePage = 1
    elseif entrada == "ajuda" or entrada == "?" then
        help()
        sleep(10)
    elseif entrada == "random" or entrada == "rd" or entrada == "rnd" then
        if PlaylistSelected >= 1 then
            local random_playlist_index = math.random(#list[PlaylistSelected].playlist) -- Random dentro da playlist
            playMusic(list[PlaylistSelected].name, list[PlaylistSelected].playlist[random_playlist_index].name, list[PlaylistSelected].playlist[random_playlist_index].link)
        else
            local random_index = math.random(#list) -- Random geral
            local random_playlist_index = math.random(#list[random_index].playlist) -- e depois na playlist
            playMusic(list[random_index].name, list[random_index].playlist[random_playlist_index].name, list[random_index].playlist[random_playlist_index].link)
        end
    end

    if PlaylistSelected >= 1 then
        exibir_pagina(inThePlaylists, list[PlaylistSelected].playlist, list[PlaylistSelected].name)
    else
        exibir_pagina(inThePage, list, "Playlists")
    end
end